package com.test;

public class PatternNestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
    int z=6;
    
    for(int i=0;i<6;i++) {
    	
    	for(int j=0;j<=i;j++) {
    		
    		System.out.print("*");
    		
    	}
    	System.out.println();
    	
    }
    
    
	}

}
